class PopularMoviesResponse {
  PopularMoviesResponse({
    this.statusCode,
    this.statusMessage,
    this.success,
      this.page, 
      this.popularMovies,
      this.totalPages, 
      this.totalResults,});

  PopularMoviesResponse.fromJson(dynamic json) {
    page = json['page'];
   statusMessage = json['status_message'];
   statusCode = json['status_code'];
   success = json['success'];
    if (json['results'] != null) {
      popularMovies = [];
      json['results'].forEach((v) {
        popularMovies?.add(Movie.fromJson(v));
      });
    }
    totalPages = json['total_pages'];
    totalResults = json['total_results'];
  }
  String? statusCode;
  String? statusMessage;
  bool? success;
  num? page;
  List<Movie>? popularMovies;
  num? totalPages;
  num? totalResults;
PopularMoviesResponse copyWith({  num? page,
  List<Movie>? results,
  num? totalPages,
  num? totalResults,
}) => PopularMoviesResponse(  page: page ?? this.page,
  popularMovies: results ?? this.popularMovies,
  totalPages: totalPages ?? this.totalPages,
  totalResults: totalResults ?? this.totalResults,
);
  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['page'] = page;
    if (popularMovies != null) {
      map['results'] = popularMovies?.map((v) => v.toJson()).toList();
    }
    map['total_pages'] = totalPages;
    map['total_results'] = totalResults;
    return map;
  }

}